    //if (!style.isAFD1C) { fail.s("doesnt follow myMain function format"); }
    if (style.hasFIF) { fail.s("don't declare functions inside other functions", (2/5)); }
    //if (style.nAFL > 5 ) { fail.s("We need 4 or fewer for loops", (1/10) ); }
