    if (style.nTFD < 4) { fail.s("We need at least 4 functions declared and used", (2/5) ); }
    fail.s("style will be manually graded");
  }
  // commit points if any
  pass.f("passed functional correctness");
  pass.s("passed style correctness");
}  

